import React, { useState, useEffect, useMemo } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

import Header from "../components/Header";
import WeatherImage from "../components/WeatherImage";

import City from "../components/City";

function Home() {

  const history = useHistory();
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState("Jakarta");

  const [cities, setCities] = useState([
    {
      name
    }
  ]
    )

  useEffect(() => {
    // console.log(process.env.REACT_APP_WEATHER_KEY);
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${process.env.REACT_APP_WEATHER_KEY}`
      )
      .then(function (response) {
        // Succesful request
        const weather = response.data;
        setWeatherData(weather);
      })
      .catch(function (error) {
        // The best practice in coding is to not use console.log
        console.warn(error);
      });
  }, [city]);

  useEffect(() => {
    const searchParams = history.location.search;
    const urlParams = new URLSearchParams(searchParams);
    const city = urlParams.get("city");
    if (city) {
      setCity(city);
    }
  }, [history]);

  const { currentTemp } = useMemo(() => {
    let currentTemp = "";
    if (weatherData) {
      currentTemp = `${Math.round(weatherData.main.temp)}°C`;
    }
    return {
      currentTemp,
    };
  }, [weatherData]);

  console.log("weatherData", weatherData);
  console.log("current")

  return (
    // Container
    <div className="flex flex-col h-screen bg-green-200 font-serif">
      <City cityName={city} temp={currentTemp} color={"bg-yellow-500"} />

      <City cityName={city} temp={currentTemp} color={"bg-blue-500"} />

      <City /* cityName={"Tokyo"} temp={"9°C"} color={"bg-red-400"} */ />

      <City /* cityName={"Dubai"} temp={"23°C"} color={"bg-indigo-300"} */ />

      <City /* cityName={"Kuala Lumpur"} temp={"27°C"} color={"bg-pink-300"} */ />
    </div>
  );
}

export default Home;
