import React, { useState, useEffect, useMemo } from "react";

// TODO
// - implement API
// - add props to details screen
// - style the details screen

function Details() {

  const history = useHistory();
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState("Jakarta");

  useEffect(() => {
    // console.log(process.env.REACT_APP_WEATHER_KEY);
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${process.env.REACT_APP_WEATHER_KEY}`
      )
      .then(function (response) {
        // Succesful request
        const weather = response.data;
        setWeatherData(weather);
      })
      .catch(function (error) {
        // The best practice in coding is to not use console.log
        console.warn(error);
      });
  }, [city]);

  useEffect(() => {
    const searchParams = history.location.search;
    const urlParams = new URLSearchParams(searchParams);
    const city = urlParams.get("city");
    if (city) {
      setCity(city);
    }
  }, [history]);

  const { currentTemp } = useMemo(() => {
    let currentTemp = "";
    if (weatherData) {
      currentTemp = `${Math.round(weatherData.main.temp)}°C`;
    }
    return {
      currentTemp,
    };
  }, [weatherData]);
  return (
    // Container
    <div className="flex flex-col items-center h-screen bg-green-200">
      <div className="p-8 text-2xl font-bold">Weather in CityName</div>

      <div className="flex flex-col p-8 m-4 border-2 rounded-md border-gray-700 items-center">
        <div>haze</div>
        <div>Current Temperature : 26</div>
      </div>

      <div>High Temperature : 26</div>
      <div>Cloudiness : 20%</div>
      <div>Low Temperature : 18</div>
      <div>Humidity : 83%</div>
      <div>Wind Speed : 3.1 mph</div>
    </div>
  );
}

export default Details;
